import * as React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { IPokerHomepageContainerProps } from '../../shared';
import { updateClientId, updateClientName } from './poker-homepage-actions'; 
import { pokerHomepageSelector } from './poker-homepage-selectors';

class PokerHomepageContainer extends React.Component<IPokerHomepageContainerProps> {
	private handleChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
		this.props.updateClientName(event);
  }

  private handleSubmit = (event: React.ChangeEvent<HTMLInputElement>): void => {
    alert('A name was submitted: ' + event);
  }

	render(): JSX.Element {
		return(
			<div>
				<div>Please enter your details</div>
				<div>
					<form>
					<label>
						<div>
							Name: <input type="text" onChange={this.handleChange} />
						</div>
						<div>
							User ID: <input type="text" />
						</div>
					</label>
					<input type="submit" value="Submit" />
					</form>
				</div>
			</div>
		);
	}
}
/*
const mapDispatchToProps = {
	updateClientId,
	updateClientName
};
*/
const mapDispatchToProps = dispatch => {
  return {
    // dispatching plain actions
    updateClientId: () => dispatch( updateClientId ),
    updateClientName: () => dispatch( updateClientName )
  }
}

export default withRouter(
	connect(
		pokerHomepageSelector,
		mapDispatchToProps
	)(PokerHomepageContainer)
);

export { PokerHomepageContainer };
